package com.xebia.irrigation.model;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.*;import org.hibernate.annotations.OnDelete;import org.hibernate.annotations.OnDeleteAction;import org.hibernate.annotations.Parent;import org.springframework.stereotype.Component;import javax.persistence.*;import javax.validation.constraints.NotBlank;import java.time.ZonedDateTime;import java.util.Objects;@Data@AllArgsConstructor@NoArgsConstructor@Component@Table(name = "slots")@Entitypublic class Slot {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "slotGenerator")    @NonNull    Long slotId;    @NonNull    int startHrs;    @NonNull    String irrigationStatus;    ZonedDateTime lastUpdatedDate;    @ManyToOne(fetch = FetchType.EAGER)    @JoinColumn(name = "PLOT_ID")    @JsonIgnore    private Plot plot;}